
var slides=document.querySelectorAll(".slide__item");var label=document.querySelectorAll(".slide__radiolabel");var currentSlide=0;var next=document.querySelector('.slider__btn-next');var previous=document.querySelector('.slider__btn-prev');var slideInterval=setInterval(nextSlide,5000);function pauseSlideshow(){clearInterval(slideInterval);};label[0].style.backgroundColor="transparent";next.onclick=function(){pauseSlideshow();nextSlide();};previous.onclick=function(){pauseSlideshow();previousSlide();};for(var i=0;i<label.length;i++){label[i].onclick=function(){pauseSlideshow();goToSlide(this.id);};};function nextSlide(){goToSlide(currentSlide+1);};function previousSlide(){goToSlide(currentSlide-1);};function goToSlide(n){label[currentSlide].style.backgroundColor="";slides[currentSlide].classList.remove("slide__item--show");currentSlide=(n+slides.length)%slides.length;label[currentSlide].style.backgroundColor="transparent";slides[currentSlide].classList.add("slide__item--show");};var selectLanguage=document.getElementById("selectLanguage");var selectCurrency=document.getElementById("selectCurrency");selectLanguage.addEventListener("change",function(){document.getElementById("iconLanguage").setAttribute("data","img/"+this.value+".svg");});selectCurrency.addEventListener("change",function(){document.getElementById("iconCurrency").setAttribute("data","img/"+this.value+".svg");});