
var slides=document.querySelectorAll(".slide__item");var radio=document.querySelectorAll(".slide__radio");var currentSlide=0;var next=document.querySelector('.slider__btn-next');var previous=document.querySelector('.slider__btn-prev');var slideInterval=setInterval(nextSlide,3000);function pauseSlideshow(){clearInterval(slideInterval);};next.onclick=function(){pauseSlideshow();nextSlide();};previous.onclick=function(){pauseSlideshow();previousSlide();};for(var i=0;i<radio.length;i++){radio[i].onclick=function(){pauseSlideshow();goToSlide(this.id);};};function nextSlide(){goToSlide(currentSlide+1);};function previousSlide(){goToSlide(currentSlide-1);};function goToSlide(n){radio[currentSlide].removeAttribute("checked");slides[currentSlide].classList.remove("slide__item--show");currentSlide=(n+slides.length)%slides.length;radio[currentSlide].setAttribute("checked",true);slides[currentSlide].classList.add("slide__item--show");};